export const ViewSwitcher: React.ComponentType<ViewSwitcherProps> = ViewSwitcherBase;
import * as React from 'react';
import {
  Plugin, Template, TemplatePlaceholder, TemplateConnector, PluginComponents,
} from '@devexpress/dx-react-core';
import { createClickHandlers, memoize } from '@devexpress/dx-core';
import {
  POSITION_START, POSITION_END, VERTICAL_TYPE,
  getVerticalRectByAppointmentData, calculateRectByDateAndGroupIntervals,
  getAppointmentStyle, HORIZONTAL_TYPE, getHorizontalRectByAppointmentData,
  isAllDayElementsMetaActual, isTimeTableElementsMetaActual,
  HORIZONTAL_GROUP_ORIENTATION, VIEW_TYPES, getGroupsLastRow, Rect,
} from '@devexpress/dx-scheduler-core';

import { AppointmentsProps } from '../types';

const AppointmentPlaceholder = params => <TemplatePlaceholder name="appointment" params={params} />;

const renderAppointments = rects => rects.map(({
  dataItem, type: rectType, fromPrev, toNext,
  durationType, resources, key, ...geometry
}) => (
  <AppointmentPlaceholder
    key={key}
    type={rectType}
    data={dataItem}
    fromPrev={fromPrev}
    toNext={toNext}
    durationType={durationType}
    resources={resources}
    style={getAppointmentStyle(geometry as Rect)}
  />
));

const pluginDependencies = [
  { name: 'DayView', optional: true },
  { name: 'WeekView', optional: true },
  { name: 'MonthView', optional: true },
];

class AppointmentsBase extends React.PureComponent<AppointmentsProps> {
  static components: PluginComponents = {
    splitIndicatorComponent: 'SplitIndicator',
    containerComponent: 'Container',
    appointmentComponent: 'Appointment',
    appointmentContentComponent: 'AppointmentContent',
    recurringIconComponent: 'RecurringIcon',
  };
  static defaultProps: Partial<AppointmentsProps> = {
    placeAppointmentsNextToEachOther: false,
  };

  updateTimeTableAppointments = memoize((
    timeTableAppointments, viewCellsData, timeTableElementsMeta, currentView,
    startViewDate, endViewDate, cellDuration, groups, getGroupOrientation, groupByDate,
    placeAppointmentsNextToEachOther,
  ) => {
    if (!isTimeTableElementsMetaActual(viewCellsData, timeTableElementsMeta)) return null;

    const groupOrientation = getGroupOrientation
      ? getGroupOrientation(currentView?.name)
      : HORIZONTAL_GROUP_ORIENTATION;
    const groupCount = groups ? getGroupsLastRow(groups).length : 1;

    let appointmentType = { growDirection: VERTICAL_TYPE, multiline: false };
    let getRects = getVerticalRectByAppointmentData as any;
    if (currentView.type === VIEW_TYPES.MONTH) {
      appointmentType = { growDirection: HORIZONTAL_TYPE, multiline: true };
      getRects = getHorizontalRectByAppointmentData;
    }

    return renderAppointments(calculateRectByDateAndGroupIntervals(
      appointmentType, timeTableAppointments, getRects,
      {
        startViewDate, endViewDate, cellDuration,
        viewCellsData, cellElementsMeta: timeTableElementsMeta,
        placeAppointmentsNextToEachOther,
      },
      {
        groupOrientation,
        groupedByDate: groupByDate?.(currentView?.name),
        groupCount,
      },
    ));
  });

  updateAllDayAppointments = memoize((
    allDayAppointments, viewCellsData, allDayElementsMeta, currentView,
    startViewDate, endViewDate, groups, getGroupOrientation, groupByDate,
  ) => {
    const groupOrientation = getGroupOrientation
      ? getGroupOrientation(currentView?.name)
      : HORIZONTAL_GROUP_ORIENTATION;
    const groupCount = groups ? getGroupsLastRow(groups).length : 1;

    if (!isAllDayElementsMetaActual(
      viewCellsData, allDayElementsMeta, groupOrientation, groupCount,
    )) {
      return null;
    }

    return renderAppointments(calculateRectByDateAndGroupIntervals(
      { growDirection: HORIZONTAL_TYPE,  multiline: false },
      allDayAppointments,
      getHorizontalRectByAppointmentData,
      {
        startViewDate, endViewDate,
        viewCellsData, cellElementsMeta: allDayElementsMeta,
      },
      {
        groupOrientation,
        groupedByDate: groupByDate?.(currentView?.name),
        groupCount,
      },
    ));
  });

  render() {
    const {
      splitIndicatorComponent: SplitIndicator,
      appointmentComponent: Appointment,
      appointmentContentComponent: AppointmentContent,
      containerComponent: Container,
      recurringIconComponent,
      placeAppointmentsNextToEachOther,
    } = this.props;

    return (
      <Plugin
        name="Appointments"
        dependencies={pluginDependencies}
      >
        <Template
          name="timeTableAppointmentLayer"
        >
          <TemplateConnector>
            {({
              timeTableAppointments, viewCellsData, timeTableElementsMeta, currentView,
              startViewDate, endViewDate, cellDuration, groupOrientation,  groups, groupByDate,
            }) => this.updateTimeTableAppointments(
              timeTableAppointments, viewCellsData, timeTableElementsMeta, currentView,
              startViewDate, endViewDate, cellDuration, groups, groupOrientation, groupByDate,
              placeAppointmentsNextToEachOther,
            )}
          </TemplateConnector>
        </Template>
        <Template
          name="allDayAppointmentLayer"
        >
          <TemplateConnector>
            {({
              allDayAppointments, viewCellsData, allDayElementsMeta,
              startViewDate, endViewDate, groupOrientation, currentView, groups, groupByDate,
            }) => this.updateAllDayAppointments(
              allDayAppointments, viewCellsData, allDayElementsMeta, currentView,
              startViewDate, endViewDate, groups, groupOrientation, groupByDate,
            )}
          </TemplateConnector>
        </Template>
        <Template
          name="appointment"
        >
          {({ style, ...params }: any) => (
            <TemplateConnector>
              {({ formatDate }) => (
                <Container style={style}>
                  <TemplatePlaceholder
                    name="appointmentTop"
                    params={{ data: params.data, type: params.type, slice: params.fromPrev }}
                  />
                  <TemplatePlaceholder
                    name="appointmentContent"
                    params={{ ...params, formatDate }}
                  />
                  <TemplatePlaceholder
                    name="appointmentBottom"
                    params={{ data: params.data, type: params.type, slice: params.toNext }}
                  />
                </Container>
              )}
            </TemplateConnector>
          )}
        </Template>

        <Template name="appointmentContent">
          {({
            onClick, onDoubleClick, formatDate,
            data, type, fromPrev, toNext,
            durationType, resources, forwardedRef,
            ...restParams
          }: any) => (
            <Appointment
              forwardedRef={forwardedRef}
              data={data}
              resources={resources}
              {...createClickHandlers(onClick, onDoubleClick)}
              {...restParams}
            >
              {fromPrev && <SplitIndicator position={POSITION_START} appointmentType={type} />}
              <AppointmentContent
                data={data}
                type={type}
                durationType={durationType}
                recurringIconComponent={recurringIconComponent}
                formatDate={formatDate}
                resources={resources}
              />
              {toNext && <SplitIndicator position={POSITION_END} appointmentType={type} />}
            </Appointment>
          )}
        </Template>
      </Plugin>
    );
  }
}

/** A plugin that renders appointments. */
export const Appointments: React.ComponentType<AppointmentsProps> = AppointmentsBase;
import * as React from 'react';
import { getMessagesFormatter, memoize } from '@devexpress/dx-core';
import {
  Getter,
  Plugin,
  Template,
  TemplatePlaceholder,
  TemplateConnector,
  PluginComponents,
} from '@devexpress/dx-react-core';
import {
  allDayCells, calculateAllDayDateIntervals,
  VERTICAL_GROUP_ORIENTATION, VIEW_TYPES,
} from '@devexpress/dx-scheduler-core';
import moment from 'moment';

import { AllDayPanelProps, AllDayPanelState } from '../types';

const isMonthView = currentView => currentView.type === VIEW_TYPES.MONTH;
const isVerticalGrouping = (
  currentView, groupOrientation,
) => groupOrientation?.(currentView.name) === VERTICAL_GROUP_ORIENTATION;

const pluginDependencies = [
  { name: 'DayView', optional: true },
  { name: 'WeekView', optional: true },
];
const defaultMessages = {
  allDay: 'All Day',
};
const AllDayAppointmentLayerPlaceholder = () =>
  <TemplatePlaceholder name="allDayAppointmentLayer" />;
const AllDayPanelPlaceholder = params => <TemplatePlaceholder name="allDayPanel" params={params} />;
const CellPlaceholder = params => <TemplatePlaceholder name="allDayPanelCell" params={params} />;
const AllDayTitlePlaceholder = params => <TemplatePlaceholder name="allDayTitle" params={params} />;

class AllDayPanelBase extends React.PureComponent<AllDayPanelProps, AllDayPanelState> {
  state: AllDayPanelState = {
    elementsMeta: {},
    previousCell: null,
    // The key has to be generated every time the Cell component is updated to rerender the Layout
    // and, consequently, update allDayElementsMeta
    layoutKey: 0,
  };
  static defaultProps: Partial<AllDayPanelProps> = {
    messages: {},
  };
  static components: PluginComponents = {
    appointmentLayerComponent: 'AppointmentLayer',
    layoutComponent: 'Layout',
    layoutContainerComponent: 'LayoutContainer',
    cellComponent: 'Cell',
    rowComponent: 'Row',
    titleCellComponent: 'TitleCell',
    containerComponent: 'Container',
  };

  static getDerivedStateFromProps(
    props: AllDayPanelProps, state: AllDayPanelState,
  ): AllDayPanelState | null {
    if (props.cellComponent !== state.previousCell) {
      return {
        ...state,
        previousCell: props.cellComponent,
        layoutKey: Math.random(),
      };
    }
    return null;
  }

  allDayCellsDataComputed = memoize(({ viewCellsData }) => allDayCells(viewCellsData));

  updateCellElementsMeta = memoize((cellElementsMeta) => {
    this.setState({ elementsMeta: cellElementsMeta });
  });

  allDayAppointmentsComputed = memoize(({
    appointments, startViewDate, endViewDate, excludedDays,
  }) => {
    const allDayLeftBound = moment(startViewDate).hours(0).minutes(0).toDate();
    const allDayRightBound = moment(endViewDate).hours(23).minutes(59).toDate();
    return calculateAllDayDateIntervals(
      appointments, allDayLeftBound, allDayRightBound, excludedDays,
    );
  });

  allDayPanelExistsComputed = memoize(({
    currentView,
  }) => !isMonthView(currentView));

  getMessageFormatter = memoize((messages, allDayPanelDefaultMessages) =>
    getMessagesFormatter({ ...allDayPanelDefaultMessages, ...messages }));

  render() {
    const {
      appointmentLayerComponent: AppointmentLayer,
      layoutComponent: Layout,
      cellComponent: Cell,
      rowComponent,
      titleCellComponent: TitleCell,
      containerComponent: Container,
      messages,
    } = this.props;
    const { elementsMeta, layoutKey } = this.state;
    const getMessage = this.getMessageFormatter(messages, defaultMessages);

    return (
      <Plugin
        name="AllDayPanel"
        dependencies={pluginDependencies}
      >
        <Getter name="allDayElementsMeta" value={elementsMeta} />
        <Getter name="allDayCellsData" computed={this.allDayCellsDataComputed} />
        <Getter name="allDayPanelExists" computed={this.allDayPanelExistsComputed} />
        <Getter
          name="allDayAppointments"
          computed={this.allDayAppointmentsComputed}
        />

        <Template name="timeTable">
          {(params: any) => (
            <TemplateConnector>
              {({ currentView, groupOrientation, allDayCellsData }) => {
                if (isMonthView(currentView)
                  || !isVerticalGrouping(currentView, groupOrientation)) {
                  return <TemplatePlaceholder params={...params} />;
                }
                return (
                  <>
                    <TemplatePlaceholder
                      params={{
                        ...params,
                        allDayCellComponent: CellPlaceholder,
                        allDayRowComponent: rowComponent,
                        allDayCellsData,
                      }}
                    />
                    <AppointmentLayer>
                      <AllDayAppointmentLayerPlaceholder />
                    </AppointmentLayer>
                  </>
                );
              }}
            </TemplateConnector>
          )}
        </Template>

        <Template name="dayScaleEmptyCell">
          <TemplateConnector>
            {({ currentView, groupOrientation }) => {
              if (isMonthView(currentView) || isVerticalGrouping(currentView, groupOrientation)) {
                return <TemplatePlaceholder />;
              }

              return (
                <AllDayTitlePlaceholder />
              );
            }}
          </TemplateConnector>
        </Template>

        <Template name="timeScale">
          {(params: any) => (
            <TemplateConnector>
              {({ currentView, groupOrientation }) => {
                if (isMonthView(currentView)
                  || !isVerticalGrouping(currentView, groupOrientation)) {
                  return <TemplatePlaceholder params={...params} />;
                }

                return (
                  <TemplatePlaceholder
                    params={{
                      ...params,
                      allDayTitleComponent: AllDayTitlePlaceholder,
                      showAllDayTitle: true,
                    }}
                  />
                );
              }}
            </TemplateConnector>
          )}
        </Template>

        <Template name="dayScale">
          <TemplatePlaceholder />
          <TemplateConnector>
            {({ currentView, groupOrientation }) => {
              if (isMonthView(currentView) || isVerticalGrouping(currentView, groupOrientation)) {
                return null;
              }

              return (
                <Container>
                  <AllDayPanelPlaceholder />
                </Container>
              );
            }}
          </TemplateConnector>
        </Template>

        <Template name="allDayPanel">
          <TemplatePlaceholder />
          <TemplateConnector>
            {({
              currentView, formatDate, allDayCellsData,
            }) => {
              if (currentView.type === VIEW_TYPES.MONTH) return null;

              return (
                <React.Fragment>
                  <Layout
                    cellComponent={CellPlaceholder}
                    rowComponent={rowComponent}
                    cellsData={allDayCellsData[0]}
                    setCellElementsMeta={this.updateCellElementsMeta}
                    formatDate={formatDate}
                    key={layoutKey}
                  />
                  <AppointmentLayer>
                    <AllDayAppointmentLayerPlaceholder />
                  </AppointmentLayer>
                </React.Fragment>
              );
            }}
          </TemplateConnector>
        </Template>

        <Template name="allDayTitle">
          {(params: any) => <TitleCell getMessage={getMessage} {...params}/>}
        </Template>
        <Template name="allDayPanelCell">
          {(params: any) => <Cell {...params} />}
        </Template>
      </Plugin>
    );
  }
}

/** A plugin that renders the All Day Panel. */
export const AllDayPanel: React.ComponentType<AllDayPanelProps> = AllDayPanelBase;
import * as React from 'react';
import {
  Getter,
  Action,
  Plugin,
  createStateHelper,
  StateHelper,
  ActionFn,
} from '@devexpress/dx-react-core';
import {
  changeCurrentDate,
  setCurrentViewName,
  ChangeCurrentDatePayload,
} from '@devexpress/dx-scheduler-core';
import { ViewStateProps, ViewStateState } from '../types';
import { memoize } from '@devexpress/dx-core';

class ViewStateBase extends React.PureComponent<ViewStateProps, ViewStateState> {
  changeCurrentDate: ActionFn<ChangeCurrentDatePayload>;
  setCurrentViewName: ActionFn<string>;

  static defaultProps: Partial<ViewStateProps> = {
    defaultCurrentDate: new Date(),
  };

  constructor(props) {
    super(props);

    this.state = {
      currentDate: props.currentDate || props.defaultCurrentDate,
      currentViewName: props.currentViewName || props.defaultCurrentViewName,
    };

    const stateHelper: StateHelper = createStateHelper(
      this,
      {
        currentDate: () => {
          const { onCurrentDateChange } = this.props;
          return onCurrentDateChange;
        },
        currentViewName: () => {
          const { onCurrentViewNameChange } = this.props;
          return onCurrentViewNameChange;
        },
      },
    );

    this.changeCurrentDate = stateHelper.applyFieldReducer
      .bind(stateHelper, 'currentDate', changeCurrentDate);
    this.setCurrentViewName = stateHelper.applyFieldReducer
      .bind(stateHelper, 'currentViewName', setCurrentViewName);
  }

  static getDerivedStateFromProps(nextProps, prevState) {
    const {
      currentDate = prevState.currentDate,
      currentViewName = prevState.currentViewName,
    } = nextProps;

    return {
      currentDate,
      currentViewName,
    };
  }

  getCurrentViewComputed =  memoize(currentViewName => () => (
    currentViewName
    ? { name: currentViewName }
    : undefined
  ));

  render() {
    const { currentDate, currentViewName: stateCurrentViewName } = this.state;
    return (
      <Plugin
        name="ViewState"
      >
        <Getter name="currentDate" value={currentDate} />
        <Getter name="currentView" computed={this.getCurrentViewComputed(stateCurrentViewName)} />
        <Action name="changeCurrentDate" action={this.changeCurrentDate} />
        <Action name="setCurrentViewName" action={this.setCurrentViewName} />
      </Plugin>
    );
  }
}

/** A plugin that manages the view state. It specifies the current date and the displayed view. */
export const ViewState: React.ComponentType<ViewStateProps> = ViewStateBase;
import * as React from 'react';
import {
  Action, Plugin, Getter, createStateHelper, StateHelper, ComputedFn, ActionFn,
} from '@devexpress/dx-react-core';
import {
  addAppointment,
  cancelAddedAppointment,
  startEditAppointment,
  stopEditAppointment,
  changeAppointment,
  cancelChanges,
  changedAppointmentById,
  RECURRENCE_EDIT_SCOPE,
  preCommitChanges as preCommitChangesBase,
} from '@devexpress/dx-scheduler-core';
import { EditingStateProps, EditingStateState } from '../types';

class EditingStateBase extends React.PureComponent<EditingStateProps, EditingStateState> {
  startEditAppointment: ComputedFn;
  stopEditAppointment: (payload?: any) => void;
  changeAppointment: ComputedFn;
  cancelChangedAppointment: (payload?: any) => void;
  commitChangedAppointment: ActionFn<any>;
  addAppointment: ComputedFn;
  changeAddedAppointment: ComputedFn;
  cancelAddedAppointment: (payload?: any) => void;
  commitAddedAppointment: ComputedFn;
  commitDeletedAppointment: ActionFn<any>;

  static defaultProps: Partial<EditingStateProps> = {
    defaultEditingAppointment: undefined,
    defaultAppointmentChanges: {},
    defaultAddedAppointment: {},
    preCommitChanges: preCommitChangesBase,
  };

  constructor(props) {
    super(props);

    this.state = {
      editingAppointment: props.editingAppointment || props.defaultEditingAppointment,
      addedAppointment: props.addedAppointment || props.defaultAddedAppointment,
      appointmentChanges: props.appointmentChanges || props.defaultAppointmentChanges,
    };

    const stateHelper: StateHelper = createStateHelper(
      this,
      {
        editingAppointment: () => {
          const { onEditingAppointmentChange } = this.props;
          return onEditingAppointmentChange;
        },
        addedAppointment: () => {
          const { onAddedAppointmentChange } = this.props;
          return onAddedAppointmentChange;
        },
        appointmentChanges: () => {
          const { onAppointmentChangesChange } = this.props;
          return onAppointmentChangesChange;
        },
      },
    );

    this.addAppointment = stateHelper.applyFieldReducer
      .bind(stateHelper, 'addedAppointment', addAppointment);
    this.changeAddedAppointment = stateHelper.applyFieldReducer
      .bind(stateHelper, 'addedAppointment', changeAppointment);
    this.cancelAddedAppointment = stateHelper.applyFieldReducer
      .bind(stateHelper, 'addedAppointment', cancelAddedAppointment);

    this.startEditAppointment = stateHelper.applyFieldReducer
      .bind(stateHelper, 'editingAppointment', startEditAppointment);
    this.stopEditAppointment = stateHelper.applyFieldReducer
      .bind(stateHelper, 'editingAppointment', stopEditAppointment);

    this.changeAppointment = stateHelper.applyFieldReducer
      .bind(stateHelper, 'appointmentChanges', changeAppointment);
    this.cancelChangedAppointment = stateHelper.applyFieldReducer
      .bind(stateHelper, 'appointmentChanges', cancelChanges);

    this.commitChangedAppointment = (type = RECURRENCE_EDIT_SCOPE.CURRENT) => {
      const { appointmentChanges, editingAppointment } = this.state;
      const { onCommitChanges, preCommitChanges } = this.props;
      if (!editingAppointment) {
        return;
      }
      const changes = !editingAppointment.rRule
        ? { changed: changedAppointmentById(appointmentChanges, editingAppointment.id!) }
        : preCommitChanges!(appointmentChanges, editingAppointment, type);

      onCommitChanges(changes);
      this.cancelChangedAppointment();
      this.stopEditAppointment();
    };

    this.commitAddedAppointment = () => {
      const { onCommitChanges } = this.props;
      const { addedAppointment: stateAddedAppointment } = this.state;
      onCommitChanges({
        added: stateAddedAppointment,
      });
    };

    this.commitDeletedAppointment = ({ deletedAppointmentData, type = 'current' }) => {
      const { onCommitChanges, preCommitChanges } = this.props;

      const changes = deletedAppointmentData.rRule
        ? preCommitChanges!(null, deletedAppointmentData, type)
        : { deleted: deletedAppointmentData.id };
      onCommitChanges(changes);
    };
  }

  static getDerivedStateFromProps(nextProps, prevState) {
    const {
      editingAppointment = prevState.editingAppointment,
      appointmentChanges = prevState.appointmentChanges,
      addedAppointment = prevState.addedAppointment,
    } = nextProps;

    return {
      editingAppointment,
      appointmentChanges,
      addedAppointment,
    };
  }

  render() {
    const { addedAppointment, editingAppointment, appointmentChanges } = this.state;

    return (
      <Plugin
        name="EditingState"
      >
        <Getter name="editingAppointment" value={editingAppointment} />
        <Action name="startEditAppointment" action={this.startEditAppointment} />
        <Action name="stopEditAppointment" action={this.stopEditAppointment} />

        <Getter name="appointmentChanges" value={appointmentChanges} />
        <Action name="changeAppointment" action={this.changeAppointment} />
        <Action name="cancelChangedAppointment" action={this.cancelChangedAppointment} />
        <Action name="commitChangedAppointment" action={this.commitChangedAppointment} />

        <Getter name="addedAppointment" value={addedAppointment} />
        <Action name="addAppointment" action={this.addAppointment} />
        <Action name="changeAddedAppointment" action={this.changeAddedAppointment} />
        <Action name="cancelAddedAppointment" action={this.cancelAddedAppointment} />
        <Action name="commitAddedAppointment" action={this.commitAddedAppointment} />

        <Action name="commitDeletedAppointment" action={this.commitDeletedAppointment} />
      </Plugin>
    );
  }
}

/** A plugin that manages the scheduler appointment editing state. */
export const EditingState: React.ComponentType<EditingStateProps> = EditingStateBase;
import * as React from 'react';
import {
  Plugin,
  Template,
  TemplatePlaceholder,
  TemplateConnector,
  createStateHelper,
  StateHelper,
  PluginComponents,
  Action,
} from '@devexpress/dx-react-core';
import {
  OPEN_COMMAND_BUTTON,
  CLOSE_COMMAND_BUTTON,
  DELETE_COMMAND_BUTTON,
  setAppointmentMeta,
  AppointmentMeta,
  TOGGLE_APPOINTMENT_TOOLTIP_VISIBILITY,
  getAppointmentResources,
} from '@devexpress/dx-scheduler-core';

import { AppointmentTooltipProps, AppointmentTooltipState, Appointments } from '../types';

const pluginDependencies = [
  { name: 'Appointments' },
  { name: 'EditingState', optional: true },
  { name: 'EditRecurrenceMenu', optional: true },
  { name: 'IntegratedEditing', optional: true },
];

const commandButtonIds = {
  open: OPEN_COMMAND_BUTTON,
  close: CLOSE_COMMAND_BUTTON,
  delete: DELETE_COMMAND_BUTTON,
};

class AppointmentTooltipBase extends React.PureComponent<
  AppointmentTooltipProps, AppointmentTooltipState
> {
  toggleVisibility: (payload?: any) => void;
  setAppointmentMeta: (appointmentMeta: AppointmentMeta) => void;
  onAppointmentClick: (appointmentMeta: AppointmentMeta) => void;

  static defaultProps: Partial<AppointmentTooltipProps> = {
    showOpenButton: false,
    showDeleteButton: false,
    showCloseButton: false,
  };
  static components: PluginComponents = {
    layoutComponent: 'Layout',
    headerComponent: 'Header',
    contentComponent: 'Content',
    commandButtonComponent: 'CommandButton',
    recurringIconComponent: 'RecurringIcon',
  };

  constructor(props) {
    super(props);

    this.state = {
      visible: props.visible,
      appointmentMeta: props.appointmentMeta,
    };

    const stateHelper: StateHelper = createStateHelper(
      this,
      {
        visible: () => {
          const { onVisibilityChange } = this.props;
          return onVisibilityChange;
        },
        appointmentMeta: () => {
          const { onAppointmentMetaChange } = this.props;
          return onAppointmentMetaChange;
        },
      },
    );

    const toggleVisibility = () => {
      const { visible: isOpen } = this.state;
      return !isOpen;
    };
    this.toggleVisibility = stateHelper.applyFieldReducer
      .bind(stateHelper, 'visible', toggleVisibility);
    this.setAppointmentMeta = stateHelper.applyFieldReducer
      .bind(stateHelper, 'appointmentMeta', setAppointmentMeta);
    this.onAppointmentClick = ({ target, data }) => {
      this.setAppointmentMeta({ target, data });
      this.toggleVisibility();
    };
  }

  static getDerivedStateFromProps(nextProps, prevState) {
    const {
      visible = prevState.visible,
      appointmentMeta = prevState.appointmentMeta,
    } = nextProps;
    return {
      appointmentMeta,
      visible,
    };
  }

  render() {
    const {
      showOpenButton,
      showDeleteButton,
      showCloseButton,
      layoutComponent: Layout,
      headerComponent,
      contentComponent,
      commandButtonComponent,
      recurringIconComponent,
    } = this.props;
    const { visible, appointmentMeta } = this.state;

    return (
      <Plugin
        name="AppointmentTooltip"
        dependencies={pluginDependencies}
      >
        <Action name={TOGGLE_APPOINTMENT_TOOLTIP_VISIBILITY} action={this.toggleVisibility} />

        <Template name="timeTable">
          <TemplatePlaceholder />
          <TemplateConnector>
            {({
              formatDate, resources, plainResources,
            }, {
              finishDeleteAppointment, openDeleteConfirmationDialog,
            }) => {
              const onDeleteButtonClick = () => {
                if (openDeleteConfirmationDialog) {
                  openDeleteConfirmationDialog({
                    hideActionName: TOGGLE_APPOINTMENT_TOOLTIP_VISIBILITY,
                    appointmentData: appointmentMeta.data,
                  });
                } else {
                  this.toggleVisibility();
                  finishDeleteAppointment(appointmentMeta.data);
                }
              };
              return (
                <TemplatePlaceholder
                  name="tooltip"
                  params={{
                    commandButtonComponent,
                    recurringIconComponent,
                    showOpenButton,
                    showDeleteButton,
                    showCloseButton,
                    headerComponent,
                    contentComponent,
                    appointmentMeta,
                    appointmentResources: appointmentMeta ? getAppointmentResources(
                      appointmentMeta.data as any, resources, plainResources,
                    ) : [],
                    visible,
                    onHide: this.toggleVisibility,
                    commandButtonIds,
                    ...finishDeleteAppointment && {
                      onDeleteButtonClick,
                    },
                    formatDate,
                  }}
                />
              );
            }}
          </TemplateConnector>
        </Template>

        <Template name="tooltip">
          {(params: any) => <Layout {...params} />}
        </Template>

        <Template name="appointment">
          {(params: Appointments.AppointmentProps) => (
            <TemplatePlaceholder
              params={{
                ...params,
                onClick: ({ target, data }) =>
                 this.onAppointmentClick({ target, data }),
              }}
            />
          )}
        </Template>
      </Plugin>
    );
  }
}

// tslint:disable: max-line-length
/** The AppointmentTooltip plugin allows you to display information about an appointment in a tooltip. */
export const AppointmentTooltip: React.ComponentType<AppointmentTooltipProps> = AppointmentTooltipBase;
import * as React from 'react';
import { getMessagesFormatter, memoize } from '@devexpress/dx-core';
import {
  Plugin,
  Template,
  createStateHelper,
  StateHelper,
  TemplateConnector,
  TemplatePlaceholder,
  PluginComponents,
  Action,
} from '@devexpress/dx-react-core';
import {
  setAppointmentData,
  isAllDayCell,
  callActionIfExists,
  AppointmentModel,
  TOGGLE_APPOINTMENT_FORM_VISIBILITY,
  getAppointmentResources,
  ValidResourceInstance,
  checkMultipleResourceFields,
} from '@devexpress/dx-scheduler-core';

import {
  AppointmentFormProps, AppointmentFormState, AppointmentTooltip, Appointments,
} from '../types';

const addDoubleClickToCell = (
  title, startDate, endDate, groupingInfo, resources,
  allDay, openFormHandler, addAppointment, params,
) => {
  const resourceFields = !!groupingInfo
    ? groupingInfo.reduce((acc, currentGroup) => (
      { ...acc, [currentGroup.fieldName]: currentGroup.id }
    ), {}) : {};
  const validResourceFields = resources
    ? checkMultipleResourceFields(resourceFields, resources)
    : resourceFields;

  const newAppointmentData = {
    title,
    startDate,
    endDate,
    allDay,
    ...validResourceFields,
  };

  return (
    <TemplatePlaceholder
      params={{
        ...params,
        onDoubleClick: () => {
          openFormHandler(newAppointmentData);
          callActionIfExists(addAppointment,
            { appointmentData: newAppointmentData });
        },
      }}
    />
  );
};

const defaultMessages = {
  allDayLabel: 'All Day',
  titleLabel: 'Title',
  commitCommand: 'Save',
  detailsLabel: 'Details',
  moreInformationLabel: 'More Information',
  repeatLabel: 'Repeat',
  notesLabel: 'Notes',
  never: 'Never',
  daily: 'Daily',
  weekly: 'Weekly',
  monthly: 'Monthly',
  yearly: 'Yearly',
  repeatEveryLabel: 'Repeat every',
  daysLabel: 'day(s)',
  endRepeatLabel: 'End repeat',
  onLabel: 'On',
  afterLabel: 'After',
  occurrencesLabel: 'occurrence(s)',
  weeksOnLabel: 'week(s) on:',
  monthsLabel: 'month(s)',
  ofEveryMonthLabel: 'of every month',
  theLabel: 'The',
  firstLabel: 'First',
  secondLabel: 'Second',
  thirdLabel: 'Third',
  fourthLabel: 'Fourth',
  lastLabel: 'Last',
  yearsLabel: 'year(s)',
  ofLabel: 'of ',
  everyLabel: 'Every',
};

const CommandLayoutPlaceholder = () => <TemplatePlaceholder name="commandLayout" />;
const BasicLayoutPlaceholder = () => <TemplatePlaceholder name="basicLayout" />;
const RecurrenceLayoutPlaceholder = () => <TemplatePlaceholder name="recurrenceLayout" />;

const pluginDependencies = [
  { name: 'EditingState', optional: true },
  { name: 'Appointments', optional: true },
  { name: 'AppointmentTooltip', optional: true },
  { name: 'EditRecurrenceMenu', optional: true },
  { name: 'IntegratedEditing', optional: true },
];

const prepareChanges = (
  appointmentData, editingAppointment,
  addedAppointment, appointmentChanges,
  resources, plainResources,
) => {
  const isNew = !editingAppointment;
  const changedAppointment = {
    ...appointmentData,
    ...appointmentChanges,
    ...isNew && addedAppointment,
  };
  const appointmentResources = getAppointmentResources(
    changedAppointment, resources, plainResources,
  );
  const isFormEdited = isNew || Object.getOwnPropertyNames(appointmentChanges).length !== 0;
  return { changedAppointment, appointmentResources, isNew, isFormEdited };
};

const isFormFullSize = (
  isFormVisible, changedAppointmentRRule, previousAppointmentRRule,
) => !!changedAppointmentRRule || (!isFormVisible && !!previousAppointmentRRule);

class AppointmentFormBase extends React.PureComponent<AppointmentFormProps, AppointmentFormState> {
  toggleVisibility: (payload?: any) => void;
  setAppointmentData: (payload: any) => void;
  openFormHandler: (payload: AppointmentModel) => void;
  container = React.createRef();

  static defaultProps: Partial<AppointmentFormProps> = {
    messages: {},
    readOnly: false,
    onVisibilityChange: () => undefined,
    onAppointmentDataChange: () => undefined,
  };
  static components: PluginComponents = {
    overlayComponent: 'Overlay',
    layoutComponent: 'Layout',
    commandLayoutComponent: 'CommandLayout',
    commandButtonComponent: 'CommandButton',
    basicLayoutComponent: 'BasicLayout',
    textEditorComponent: 'TextEditor',
    labelComponent: 'Label',
    dateEditorComponent: 'DateEditor',
    booleanEditorComponent: 'BooleanEditor',
    selectComponent: 'Select',
    recurrenceLayoutComponent: 'RecurrenceLayout',
    radioGroupComponent: 'RadioGroup',
    weeklyRecurrenceSelectorComponent: 'WeeklyRecurrenceSelector',
    resourceEditorComponent: 'ResourceEditor',
    containerComponent: 'Container',
  };

  constructor(props) {
    super(props);

    this.state = {
      visible: props.visible,
      appointmentData: props.appointmentData || {},
      previousAppointment: props.appointmentData || {},
    };

    const stateHelper: StateHelper = createStateHelper(
      this,
      {
        visible: () => {
          const { onVisibilityChange } = this.props;
          return onVisibilityChange;
        },
        appointmentData: () => {
          const { onAppointmentDataChange } = this.props;
          return onAppointmentDataChange;
        },
      },
    );

    const toggleVisibility = () => {
      const { visible: isOpen } = this.state;
      return !isOpen;
    };
    this.toggleVisibility = stateHelper.applyFieldReducer
      .bind(stateHelper, 'visible', toggleVisibility);
    this.setAppointmentData = stateHelper.applyFieldReducer
      .bind(stateHelper, 'appointmentData', setAppointmentData);

    this.openFormHandler = (appointmentData) => {
      this.setAppointmentData({ appointmentData });
      this.toggleVisibility();
    };
  }

  static getDerivedStateFromProps(nextProps, prevState) {
    const {
      visible = prevState.visible,
      appointmentData = prevState.appointmentData,
    } = nextProps;
    return {
      appointmentData,
      visible,
    };
  }

  commitChanges = memoize((
    finishCommitAppointment, commitAddedAppointment, isNew, changedAppointment,
  ) => () =>  {
    this.toggleVisibility();
    if (isNew) {
      callActionIfExists(commitAddedAppointment, changedAppointment);
    } else if (finishCommitAppointment) {
      finishCommitAppointment();
    }
    this.setState({ previousAppointment: changedAppointment });
  });

  cancelChanges = memoize((
    openCancelConfirmationDialog, isNew, stopEditAppointment, appointmentChanges,
    changedAppointment, cancelAddedAppointment, cancelChangedAppointment,
  ) => () => {
    if (openCancelConfirmationDialog && Object.keys(appointmentChanges).length !== 0) {
      openCancelConfirmationDialog(TOGGLE_APPOINTMENT_FORM_VISIBILITY);
    } else {
      if (isNew) {
        callActionIfExists(cancelAddedAppointment, appointmentChanges);
      } else {
        callActionIfExists(stopEditAppointment, appointmentChanges);
        callActionIfExists(cancelChangedAppointment, appointmentChanges);
      }
      this.toggleVisibility();
    }
    this.setState({ previousAppointment: changedAppointment });
  });

  deleteAppointment = memoize((
    finishDeleteAppointment, appointmentData, openDeleteConfirmationDialog,
    changedAppointment, cancelAddedAppointment, cancelChangedAppointment,
    stopEditAppointment, isNew,
  ) => () => {
    if (openDeleteConfirmationDialog) {
      openDeleteConfirmationDialog({
        hideActionName: TOGGLE_APPOINTMENT_FORM_VISIBILITY, appointmentData: changedAppointment,
      });
    } else {
      callActionIfExists(finishDeleteAppointment, appointmentData);
      if (isNew) {
        callActionIfExists(cancelAddedAppointment, appointmentData);
      } else {
        callActionIfExists(cancelChangedAppointment, appointmentData);
        callActionIfExists(stopEditAppointment, appointmentData);
      }
      this.toggleVisibility();
    }
    this.setState({ previousAppointment: changedAppointment });
  });

  changeAppointmentField = memoize((isNew, changeAddedAppointment, changeAppointment) =>
    (change) => {
      if (change && change.rRule) {
        this.setState({ previousAppointment: {
          ...this.state.previousAppointment, rRule: change.rRule,
        }});
      }
      if (isNew) {
        callActionIfExists(changeAddedAppointment, { change });
      } else {
        callActionIfExists(changeAppointment, { change });
      }
    },
  );

  getMessage = memoize((menuMessages, messages) =>
    getMessagesFormatter({ ...menuMessages, ...messages }));

  render() {
    const {
      containerComponent: Container,
      overlayComponent: Overlay,
      layoutComponent: Layout,
      commandLayoutComponent: CommandLayout,
      basicLayoutComponent: BasicLayout,
      recurrenceLayoutComponent: RecurrenceLayout,
      commandButtonComponent,
      textEditorComponent,
      labelComponent,
      dateEditorComponent,
      booleanEditorComponent,
      selectComponent,
      radioGroupComponent,
      weeklyRecurrenceSelectorComponent,
      resourceEditorComponent,
      readOnly,
      messages,
    } = this.props;
    const { visible, appointmentData, previousAppointment } = this.state;
    const getMessage = this.getMessage(defaultMessages, messages);
    return (
      <Plugin
        name="AppointmentForm"
        dependencies={pluginDependencies}
      >
        <Action name={TOGGLE_APPOINTMENT_FORM_VISIBILITY} action={this.toggleVisibility} />

        <Template name="schedulerRoot">
          <TemplateConnector>
            {({
              editingAppointment,
              addedAppointment,
              appointmentChanges,

              resources,
              plainResources,
            }, {
              openCancelConfirmationDialog,

              stopEditAppointment,
              cancelAddedAppointment,
              cancelChangedAppointment,
            }) => {
              const { changedAppointment, isNew } = prepareChanges(
                appointmentData, editingAppointment,
                addedAppointment, appointmentChanges,
                resources, plainResources,
              );
              const fullSize = isFormFullSize(
                visible, changedAppointment.rRule, previousAppointment.rRule,
              );
              const onHideAction = () => visible && this.cancelChanges(
                openCancelConfirmationDialog, isNew, stopEditAppointment,
                { ...appointmentChanges, ...addedAppointment }, changedAppointment,
                cancelAddedAppointment, cancelChangedAppointment,
              )();

              return (
                <React.Fragment>
                  <Container ref={this.container} />
                  <Overlay
                    visible={visible}
                    onHide={onHideAction}
                    fullSize={fullSize}
                    target={this.container}
                  >
                    <Layout
                      basicLayoutComponent={BasicLayoutPlaceholder}
                      commandLayoutComponent={CommandLayoutPlaceholder}
                      recurrenceLayoutComponent={RecurrenceLayoutPlaceholder}
                      isRecurrence={fullSize}
                    />
                  </Overlay>
                  <TemplatePlaceholder />
                </React.Fragment>);
            }}
          </TemplateConnector>
        </Template>
        <Template name="commandLayout">
          <TemplateConnector>
            {({
              editingAppointment,
              addedAppointment,
              appointmentChanges,

              resources,
              plainResources,
            }, {
              commitAddedAppointment,
              finishCommitAppointment,
              finishDeleteAppointment,

              stopEditAppointment,
              cancelAddedAppointment,
              cancelChangedAppointment,

              openCancelConfirmationDialog,
              openDeleteConfirmationDialog,
            }) => {
              const { isNew, changedAppointment, isFormEdited } = prepareChanges(
                appointmentData, editingAppointment,
                addedAppointment, appointmentChanges,
                resources, plainResources,
              );
              const isRecurrence = isFormFullSize(
                visible, changedAppointment.rRule, previousAppointment.rRule,
              );
              return (
                <CommandLayout
                  commandButtonComponent={commandButtonComponent}
                  onCommitButtonClick={this.commitChanges(
                    finishCommitAppointment, commitAddedAppointment, isNew, changedAppointment,
                  )}
                  onCancelButtonClick={this.cancelChanges(
                    openCancelConfirmationDialog, isNew, stopEditAppointment,
                    { ...appointmentChanges, ...addedAppointment }, changedAppointment,
                    cancelAddedAppointment, cancelChangedAppointment,
                  )}
                  onDeleteButtonClick={this.deleteAppointment(
                    finishDeleteAppointment, appointmentData, openDeleteConfirmationDialog,
                    changedAppointment, cancelAddedAppointment, cancelChangedAppointment,
                    stopEditAppointment, isNew,
                  )}
                  getMessage={getMessage}
                  readOnly={readOnly}
                  fullSize={isRecurrence}
                  disableSaveButton={!isFormEdited}
                  hideDeleteButton={isNew}
                />
              );
            }}
          </TemplateConnector>
        </Template>
        <Template name="basicLayout">
          <TemplateConnector>
            {({
              editingAppointment,
              addedAppointment,
              appointmentChanges,
              locale,

              resources,
              plainResources,
            }, {
              changeAppointment,
              changeAddedAppointment,
            }) => {
              const { isNew, changedAppointment, appointmentResources } = prepareChanges(
                appointmentData, editingAppointment,
                addedAppointment, appointmentChanges,
                resources, plainResources,
              );
              return (
                <BasicLayout
                  locale={locale}
                  appointmentData={visible ? changedAppointment : previousAppointment}
                  onFieldChange={this.changeAppointmentField(
                    isNew, changeAddedAppointment, changeAppointment,
                  )}
                  getMessage={getMessage}
                  readOnly={readOnly}
                  textEditorComponent={textEditorComponent}
                  dateEditorComponent={dateEditorComponent}
                  booleanEditorComponent={booleanEditorComponent}
                  selectComponent={selectComponent}
                  labelComponent={labelComponent}
                  resourceEditorComponent={resourceEditorComponent}
                  fullSize={!changedAppointment.rRule}
                  resources={resources}
                  appointmentResources={appointmentResources as Array<ValidResourceInstance>}
                />
              );
            }}
          </TemplateConnector>
        </Template>

        <Template name="recurrenceLayout">
          <TemplateConnector>
            {({
              editingAppointment,
              addedAppointment,
              appointmentChanges,
              formatDate,
              locale,
              firstDayOfWeek,
            }, {
              changeAddedAppointment,
              changeAppointment,
            }) => {
              const { isNew, changedAppointment } = prepareChanges(
                appointmentData, editingAppointment,
                addedAppointment, appointmentChanges,
                undefined, undefined,
              );
              const isRecurrenceLayoutVisible = isFormFullSize(
                visible, changedAppointment.rRule, previousAppointment.rRule,
              );
              const correctedAppointment = !changedAppointment.rRule
                ? { ...changedAppointment, rRule: previousAppointment.rRule } : changedAppointment;

              return (
                <RecurrenceLayout
                  locale={locale}
                  appointmentData={visible ? correctedAppointment : previousAppointment}
                  onFieldChange={this.changeAppointmentField(
                    isNew, changeAddedAppointment, changeAppointment,
                  )}
                  getMessage={getMessage}
                  readOnly={readOnly}
                  formatDate={formatDate}
                  textEditorComponent={textEditorComponent}
                  dateEditorComponent={dateEditorComponent}
                  radioGroupComponent={radioGroupComponent}
                  weeklyRecurrenceSelectorComponent={weeklyRecurrenceSelectorComponent}
                  labelComponent={labelComponent}
                  selectComponent={selectComponent}
                  visible={isRecurrenceLayoutVisible}
                  firstDayOfWeek={firstDayOfWeek}
                />
              );
            }}
          </TemplateConnector>
        </Template>

        <Template name="tooltip">
          {(params: AppointmentTooltip.LayoutProps) => (
            <TemplateConnector>
              {(getters, { startEditAppointment }) => (
                <TemplatePlaceholder
                  params={{
                    ...params,
                    onOpenButtonClick: () => {
                      this.openFormHandler(params.appointmentMeta!.data);
                      callActionIfExists(startEditAppointment, params.appointmentMeta!.data);
                    },
                  }}
                />
              )}
            </TemplateConnector>
          )}
        </Template>

        <Template name="appointment">
          {(params: Appointments.AppointmentProps) => (
            <TemplateConnector>
              {(getters, { startEditAppointment }) => (
                <TemplatePlaceholder
                  params={{
                    ...params,
                    onDoubleClick: () => {
                      this.openFormHandler(params.data);
                      callActionIfExists(startEditAppointment, params.data);
                    },
                  }}
                />
              )}
            </TemplateConnector>
          )}
        </Template>

        <Template name="cell">
          {(params: any) => (
            <TemplateConnector>
              {({ resources }, { addAppointment }) => addDoubleClickToCell(
                undefined, params.startDate, params.endDate, params.groupingInfo, resources,
                isAllDayCell(params.startDate, params.endDate),
                this.openFormHandler, addAppointment, params,
              )}
            </TemplateConnector>
          )}
        </Template>

        <Template name="allDayPanelCell">
          {(params: any) => (
            <TemplateConnector>
              {({ resources }, { addAppointment }) => addDoubleClickToCell(
                undefined, params.startDate, params.endDate, params.groupingInfo, resources,
                true, this.openFormHandler, addAppointment, params,
              )}
            </TemplateConnector>
          )}
        </Template>
      </Plugin >
    );
  }
}

