/** A plugin that renders the dialog which allows users to confirm or to discard delete and cancel appointment actions. */
export const ConfirmationDialog: React.ComponentType<
  ConfirmationDialogProps
> = ConfirmationDialogBase;
import * as React from 'react';
import {
  Action, Plugin, Getter, StateHelper, ActionFn, createStateHelper,
} from '@devexpress/dx-react-core';
import {
  ToggleGroupPayload, toggleExpandedGroups, HORIZONTAL_GROUP_ORIENTATION,
} from '@devexpress/dx-scheduler-core';
import { GroupingStateProps, GroupingStateState } from '../types';

class GroupingStateBase extends React.PureComponent<GroupingStateProps, GroupingStateState> {
  static defaultProps = {
    defaultExpandedGroups: [],
    groupByDate: () => false,
    groupOrientation: () => HORIZONTAL_GROUP_ORIENTATION,
  };
  stateHelper: StateHelper;
  toggleGroupExpanded: ActionFn<ToggleGroupPayload>;

  constructor(props) {
    super(props);

    this.state = {
      grouping: props.grouping,
      expandedGroups: props.expandedGroups || props.defaultExpandedGroups,
    };
    this.stateHelper = createStateHelper(
      this,
      {
        expandedGroups: () => {
          const { onExpandedGroupsChange } = this.props;
          return onExpandedGroupsChange;
        },
      },
    );
    this.toggleGroupExpanded = this.stateHelper.applyReducer
      .bind(this.stateHelper, toggleExpandedGroups);
  }

  static getDerivedStateFromProps(nextProps, prevState) {
    const {
      grouping = prevState.grouping,
      expandedGroups = prevState.expandedGroups,
    } = nextProps;

    return { grouping, expandedGroups };
  }

  render() {
    const { grouping, expandedGroups } = this.state;
    const { groupByDate, groupOrientation } = this.props;

    return (
      <Plugin name="GroupingState">
        <Getter name="grouping" value={grouping} />
        <Getter name="groupByDate" value={groupByDate} />
        <Getter name="groupOrientation" value={groupOrientation} />

        <Getter name="expandedGroups" value={expandedGroups} />
        <Action name="toggleGroupExpanded" action={this.toggleGroupExpanded} />
      </Plugin>
    );
  }
}

/** A plugin that manages the grouping state. */
export const GroupingState: React.ComponentType<GroupingStateProps> = GroupingStateBase;
import * as React from 'react';
import {
  Plugin,
  Template,
  TemplateConnector,
  PluginComponents,
} from '@devexpress/dx-react-core';
import { GroupingPanelProps } from '../types';
import {
  VERTICAL_VIEW_LEFT_OFFSET, HORIZONTAL_VIEW_LEFT_OFFSET,
  HORIZONTAL_GROUP_ORIENTATION, VIEW_TYPES,
} from '@devexpress/dx-scheduler-core';

const pluginDependencies = [
  { name: 'GroupingState' },
  { name: 'IntegratedGrouping' },
  { name: 'DayView', optional: true },
  { name: 'MonthView', optional: true },
  { name: 'WeekView', optional: true },
  { name: 'ViewState', optional: true },
];

class GroupingPanelBase extends React.PureComponent<GroupingPanelProps> {
  static components: PluginComponents = {
    horizontalLayoutComponent: 'HorizontalLayout',
    verticalLayoutComponent: 'VerticalLayout',
    rowComponent: 'Row',
    cellComponent: 'Cell',
  };

  render() {
    const {
      horizontalLayoutComponent: HorizontalLayout,
      verticalLayoutComponent: VerticalLayout,
      rowComponent,
      cellComponent,
    } = this.props;

    return (
      <Plugin
        name="GroupingPanel"
        dependencies={pluginDependencies}
      >
        <Template name="groupingPanel">
          <TemplateConnector>
            {({
              viewCellsData, currentView, scrollingStrategy, allDayPanelExists,
              groupByDate, groupOrientation: getGroupOrientation, groups,
            }) => {
              const groupOrientation = getGroupOrientation(currentView?.name);
              return groupOrientation === HORIZONTAL_GROUP_ORIENTATION ? (
                <HorizontalLayout
                  rowComponent={rowComponent}
                  cellComponent={cellComponent}
                  groups={groups}
                  colSpan={viewCellsData[0].length}
                  cellStyle={{
                    left: scrollingStrategy.fixedLeftWidth ? scrollingStrategy.fixedLeftWidth
                    : currentView?.type === VIEW_TYPES.MONTH
                      ? HORIZONTAL_VIEW_LEFT_OFFSET
                      : VERTICAL_VIEW_LEFT_OFFSET,
                  }}
                  showHeaderForEveryDate={groupByDate?.(currentView && currentView.name)}
                />
              ) : (
                <VerticalLayout
                  rowComponent={rowComponent}
                  cellComponent={cellComponent}
                  groups={groups}
                  rowSpan={viewCellsData.length}
                  viewType={currentView?.type}
                  cellTextTopOffset={scrollingStrategy?.fixedTopHeight}
                  alignWithAllDayRow={allDayPanelExists}
                />
              );
            }}
          </TemplateConnector>
        </Template>
      </Plugin>
    );
  }
}

/** A plugin that renders the grouping panel used to display group names. */
export const GroupingPanel: React.ComponentType<GroupingPanelProps> = GroupingPanelBase;
import * as React from 'react';
import {
  Plugin,
  Template,
  TemplatePlaceholder,
} from '@devexpress/dx-react-core';
import {
  isMonthCell, isShadedAppointment,
  isCellShaded, getCurrentTimeIndicatorTop,
} from '@devexpress/dx-scheduler-core';
import { CurrentTimeIndicatorProps, Appointments } from '../types';

const pluginDependencies = [
  { name: 'DayView', optional: true },
  { name: 'WeekView', optional: true },
  { name: 'MonthView', optional: true },
  { name: 'DragDropProvider', optional: true },
  { name: 'Appointments', optional: true },
];

const CurrentTimeIndicatorBase: React.SFC<CurrentTimeIndicatorProps>  & {components: {
  indicatorComponent: string,
}} = ({
  indicatorComponent, shadePreviousAppointments, shadePreviousCells, updateInterval,
}) => {
  const [currentTime, setCurrentTime] = React.useState(Date.now);

  React.useEffect(() => {
    const tick = () => setCurrentTime(Date.now());
    const intervalId = (updateInterval
      ? window.setInterval(tick, updateInterval)
      : undefined
    );
    return () => window.clearInterval(intervalId);
  }, [updateInterval]);

  return (
    <Plugin
      name="CurrentTimeIndicator"
      dependencies={pluginDependencies}
    >
      <Template
        name="cell"
        predicate={({ otherMonth }: any) => !isMonthCell(otherMonth)}
      >
        {(params: any) => (
          <TemplatePlaceholder
            params={{
              ...params,
              currentTimeIndicatorPosition: getCurrentTimeIndicatorTop(params, currentTime),
              currentTimeIndicatorComponent: indicatorComponent,
            }}
          />
        )}
      </Template>
      <Template
        name="cell"
      >
        {(params: any) => (
          <TemplatePlaceholder
            params={{
              ...params,
              isShaded: isCellShaded(params, currentTime, shadePreviousCells),
            }}
          />
        )}
      </Template>
      <Template
        name="appointmentContent"
      >
        {(params: Appointments.AppointmentProps) => (
          <TemplatePlaceholder
            params={{
              ...params,
              isShaded: isShadedAppointment(
                params, currentTime, shadePreviousAppointments,
              ),
            }}
          />
        )}
      </Template>
      <Template
        name="draftAppointment"
      >
        {(params: Appointments.AppointmentProps) => (
          <TemplatePlaceholder
            params={{
              ...params,
              isShaded: isShadedAppointment(
                params, currentTime, shadePreviousAppointments,
              ),
            }}
          />
        )}
      </Template>
    </Plugin>
  );
};

CurrentTimeIndicatorBase.defaultProps = {
  updateInterval: 60000,
  shadePreviousCells: false,
  shadePreviousAppointments: false,
};

CurrentTimeIndicatorBase.components = {
  indicatorComponent: 'Indicator',
};

// tslint:disable-next-line: max-line-length
/** A plugin that renders the current time indicator and the shading that covers appointments and timetable cells up to the current time. */
export const CurrentTimeIndicator: React.ComponentType<
  CurrentTimeIndicatorProps
> = CurrentTimeIndicatorBase;
import * as React from 'react';
import { Plugin, Getter, Getters } from '@devexpress/dx-react-core';
import {
  getGroupsFromResources, expandViewCellsDataWithGroups,
  sortFilteredResources, filterResourcesByGrouping, updateGroupingWithMainResource,
  expandGroups, VERTICAL_GROUP_ORIENTATION, VIEW_TYPES,
  updateTimeTableCellElementsMeta, updateAllDayCellElementsMeta, updateTimeCellsData,
} from '@devexpress/dx-scheduler-core';
import { IntegratedGroupingProps } from '../types';

const pluginDependencies = [
  { name: 'Resources' },
  { name: 'GroupingState' },
  { name: 'DayView', optional: true },
  { name: 'MonthView', optional: true },
  { name: 'WeekView', optional: true },
];

const getViewCellsDataComputed = ({
  viewCellsData, groups, resourcesToGroupBy, groupByDate, currentView, groupOrientation,
}: Getters) => expandViewCellsDataWithGroups(
  viewCellsData, groups, resourcesToGroupBy,
  groupByDate(currentView.name), groupOrientation(currentView.name),
);
const getAllDayCellsDataComputed = ({
  allDayCellsData, groups, resourcesToGroupBy, groupByDate, currentView, groupOrientation,
}: Getters) => allDayCellsData && expandViewCellsDataWithGroups(
  allDayCellsData, groups, resourcesToGroupBy,
  groupByDate(currentView.name), groupOrientation(currentView.name),
);

const getGroupsComputed = (
  { resourcesToGroupBy }: Getters,
) => getGroupsFromResources(resourcesToGroupBy);

const getResourcesToGroupByComputed = (
  { resources, grouping }: Getters,
) => sortFilteredResources(filterResourcesByGrouping(resources, grouping), grouping);

const getGroupingComputed = (
  { grouping, resources }: Getters,
) => updateGroupingWithMainResource(grouping, resources);

const getTimeTableAppointmentsComputed = ({
  timeTableAppointments, grouping, resourcesToGroupBy,
  groups, groupByDate, currentView, excludedDays,
}: Getters) => timeTableAppointments
  && expandGroups(
    timeTableAppointments, grouping, resourcesToGroupBy, groups,
    excludedDays, groupByDate(currentView?.name) && currentView?.type === VIEW_TYPES.MONTH,
  );

const getAllDayAppointmentsComputed = ({
    allDayAppointments, grouping, resourcesToGroupBy,
    groups, groupByDate, currentView, excludedDays,
  }: Getters) => allDayAppointments &&
  expandGroups(
    allDayAppointments, grouping, resourcesToGroupBy,
    groups, excludedDays, groupByDate(currentView?.name),
  );

const getGroupByDateComputed = ({
  currentView, groupByDate, groupOrientation,
}: Getters) => groupOrientation(currentView?.name) === VERTICAL_GROUP_ORIENTATION
  ? () => false : groupByDate;

const getTimeTableElementsMetaComputed = ({
  timeTableElementsMeta, groupOrientation, groups, allDayPanelExists, viewCellsData, currentView,
}: Getters) => updateTimeTableCellElementsMeta(
  timeTableElementsMeta, groupOrientation, groups, allDayPanelExists, viewCellsData, currentView,
);

const getAllDayElementsMetaComputed = ({
  allDayElementsMeta, timeTableElementsMeta, groupOrientation, groups,
  allDayPanelExists, viewCellsData, currentView,
}: Getters) => updateAllDayCellElementsMeta(
  allDayElementsMeta, timeTableElementsMeta, groupOrientation, groups,
  allDayPanelExists, viewCellsData, currentView,
);

const getTimeCellsDataComputed = ({
  viewCellsData, timeCellsData,  currentView,
  groups, resourcesToGroupBy, groupOrientation,
}: Getters) => timeCellsData
  && updateTimeCellsData(
    viewCellsData,
    timeCellsData,
    groups,
    resourcesToGroupBy,
    groupOrientation(currentView.name),
  );

const IntegratedGroupingBase: React.SFC<IntegratedGroupingProps> = React.memo(() => (
  <Plugin
    name="IntegratedGrouping"
    dependencies={pluginDependencies}
  >
    <Getter name="groupByDate" computed={getGroupByDateComputed} />
    <Getter name="grouping" computed={getGroupingComputed} />
    <Getter name="resourcesToGroupBy" computed={getResourcesToGroupByComputed} />
    <Getter name="groups" computed={getGroupsComputed} />

    <Getter name="viewCellsData" computed={getViewCellsDataComputed} />
    <Getter name="allDayCellsData" computed={getAllDayCellsDataComputed} />
    <Getter name="timeCellsData" computed={getTimeCellsDataComputed} />

    <Getter name="timeTableAppointments" computed={getTimeTableAppointmentsComputed} />
    <Getter name="allDayAppointments" computed={getAllDayAppointmentsComputed} />

    <Getter name="allDayElementsMeta" computed={getAllDayElementsMetaComputed} />
    <Getter name="timeTableElementsMeta" computed={getTimeTableElementsMetaComputed} />
  </Plugin>
));

/** A plugin that implements grouping. */
export const IntegratedGrouping: React.ComponentType<
  IntegratedGroupingProps
> = IntegratedGroupingBase;
